import React,{useState} from 'react';
import { FaPlus,FaMinus,FaDivide,FaEquals,FaTimes,FaPercent,FaRegTrashAlt} from "react-icons/fa";



const Calculator = () => {

//   const [state1, setstate1] = useState(0);

//   const clicked =(num)=>{
//     setstate1(num);
//   }

const [state1, setstate1] = useState();
const clicked=(e)=>{

    setstate1(e.target.name);
}


    return (
        <>
            
        <div className='main_div'>
            <div className='child'>
                <h1>Calculator</h1>
            </div>


            <div className='child2'>
                <h1 > {state1} </h1>

            </div>

            <div className='child3'>

           <span name="+" onclick={clicked('+')}> <FaPlus/></span>
            <span name="-" onclick={clicked()}><FaMinus/></span>
            <span name="/" onclick={clicked()}><FaDivide/></span>
            <span name="*" onclick={clicked()}><FaTimes/></span>
            <span name="%" onclick={clicked()}><FaPercent/></span>
            <span name="0" onclick={clicked()}><FaRegTrashAlt/></span>
            <hr/>


            <div className='numbers'>
            <span name="1" onclick={clicked()}>1</span>
            <span name="2" onclick={clicked()}>2</span>
            <span name="3" onclick={clicked()}>3</span>
            <span name="4" onclick={clicked()}>4</span>
            <span name="5" onclick={clicked()}>5</span>
            <span name="6" onclick={clicked()}>6</span>
            <span name="7" onclick={clicked()}>7</span>
            <span name="8" onclick={clicked()}>8</span>
            <span name="9" onclick={clicked()}>9</span>
            <span name="0" onclick={clicked()}>0</span>
            <span name="." onclick={clicked()}>.</span>
            <span><FaEquals/></span>

            
            </div>

            </div>
        </div>

        </>
    )
}

export default Calculator;
