import React,{useState} from 'react'

const Test = () => {
    const [state, setstate] = useState();
    const [state2, setstate2] = useState([]);

    // const demo =(e)=>{
    //     setstate(e.target.value);
    //     console.log(state);
    // }
    const change=(e)=>{
        setstate(e.target.value)
    }
    const click=()=>{
        setstate2([...state,state2])
    }

    return (
        <>
           <input 
           type="text"
        //    value={state}
            onChange={change}
           />

           <button onClick={click} >Click Me</button> 
           <h1>{state2}</h1>
        </>
    )
}

export default Test
