import React,{useState} from 'react'

const Test = () => {
    const [state, setstate] = useState();
    const [state2, setstate2] = useState([]);

    // const demo =(e)=>{
    //     setstate(e.target.value);
    //     console.log(state);
    // }
    const change=(e)=>{
        setstate(e.target.value)
    }
    const click=(e)=>{
        setstate2(state2.concat(e.target.value));
        // setstate2([state2,...state]);
        setstate("");
    }

    return (
        <>
           <input 
           type="text"
           value={state}
            onChange={change}
           />

           <button onClick={click} value='1' >1</button> 
           <h1>{state2}</h1>
        </>
    )
}

export default Test;
