import React,{useState} from 'react';
import { FaPlus,FaMinus,FaDivide,FaEquals,FaTimes,FaPercent,FaRegTrashAlt} from "react-icons/fa";



const Calculator = () => {
    const [state1, setstate1] = useState();
    const [state2, setstate2] = useState([]);
    const numclick=(num)=>{
        setstate1(num);
    }
    const onchng=(e)=>{
        setstate2(e.target.value);
        
    }

    const add=(num1,num2)=>{
        const demo = num1+num2;
        
    }

    return (
        <>
            
        <div className='main_div'>
            <div className='child'>
                <h1>Calculator</h1>
                <h3></h3>
            </div>


            <div className='child2'>
                <h1 onChange={onchng}>{state1}</h1>
            </div>



            <div className='child3'>

           <span onclick={add(5,5)}> <FaPlus/></span>
            <span><FaMinus/></span>
            <span><FaDivide/></span>
            <span><FaTimes/></span>
            <span><FaPercent/></span>
            <span><FaRegTrashAlt/></span>
            <hr/>
            <div className='numbers'>
            <span onClick={()=>numclick(1)}>1</span>
            <span onClick={()=>numclick(2)}>2</span>
            <span onClick={()=>numclick(3)}>3</span>
            <span onClick={()=>numclick(4)}>4</span>
            <span onClick={()=>numclick(5)}>5</span>
            <span onClick={()=>numclick(6)}>6</span>
            <span onClick={()=>numclick(7)}>7</span>
            <span onClick={()=>numclick(8)}>8</span>
            <span onClick={()=>numclick(9)}>9</span>
            <span onClick={()=>numclick(0)}>0</span>
            <span onClick={()=>numclick(".")}>.</span>
            <span><FaEquals/></span>
            </div>

            </div>
        </div>

        </>
    )
}

export default Calculator;
